import React, { useEffect, useState } from "react";
import axios from 'axios';
import {
  Card,
  CardBody,
  CardFooter,
  Row,
  Col,
} from "reactstrap";

function User() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Fetch user data from the backend
    axios.get('http://localhost:3001/Profile', { withCredentials: true })
      .then(response => {
        setUser(response.data.user);
      })
      .catch(error => {
        console.error('Error fetching user data:', error.message);
      });
  }, []);

  return (
    <>
      <div className="content">
        <Row>
          <Col md="4">
            <Card className="card-user">
              <div className="image">
                <img alt="..." src={require("assets/img/damir-bosnjak.jpg")} />
              </div>
              <CardBody>
                <div className="author">
                  {user && (
                    <>
                      <img
                        alt="..."
                        className="avatar border-gray"
                        src={require("assets/img/mike.jpg")}
                      />
                      <h5 className="title">{user.username}</h5>
                      <p className="description">Name: {user.username}</p>
                      <p className="description">Email: {user.email}</p>
                      <p className="description">Phone: {user.phone}</p>
                    </>
                  )}
                </div>
              </CardBody>
              <CardFooter>
                <hr />
                <div className="button-container">
                  {/* You can add additional buttons or components here */}
                </div>
              </CardFooter>
            </Card>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default User;
