import React, { useEffect, useState } from "react";
import axios from 'axios';
import {
  Card,
  CardBody,
  CardFooter,
  Row,
  Col,
} from "reactstrap";

const User = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Fetch user data from session
    axios.get("/api/user/session") // Assuming your server exposes this endpoint to fetch user data from session
      .then(response => {
        setUser(response.data);
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
      });
  }, []); // Empty dependency array means this effect will run only once after component mounts

  return (
    <>
      <div className="content">
        <Row>
          <Col md="4">
            <Card className="card-user">
              <CardBody>
                <div className="author">
                  <h5 className="title">{user ? user.username : ''}</h5>
                  <p className="description">Name: {user ? user.username : ''}</p>
                  <p className="description">Email: {user ? user.email : ''}</p>
                  <p className="description">Phone: {user ? user.phone : ''}</p>
                </div>
              </CardBody>
              <CardFooter>
                <hr />
                <div className="button-container">
                  {/* You can add additional buttons or components here */}
                </div>
              </CardFooter>
            </Card>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default User;
